operation.add:
$order_table = (DB_PREFIX) ? "order" : "`order`";
$query = $this->db->query("SELECT nfe_info FROM " . DB_PREFIX . "$order_table WHERE order_id = $order_id");
$url = $this->url->link('extension/module/webmaniabrnfe/updateNFe', 'user_token=' . $this->session->data['user_token'], true);

if(isset($query->rows[0]['nfe_info'])){
 $nfe_info = @unserialize($query->rows[0]['nfe_info']);
 if (!$nfe_info) {
     $nfe_info = @unserialize(utf8_decode(base64_decode($query->rows[0]['nfe_info'])));
 }
 if (!$nfe_info) {
     $nfe_info = array();
 }
 foreach ($nfe_info as $key => $nfe) {
   if (!$nfe['url_danfe_simples']) {
     $nfe_info[$key]['url_danfe_simples'] = str_replace('/danfe/', '/danfe/simples/', $nfe['url_danfe']); 
   }
   if (!$nfe['url_danfe_etiqueta']) {
     $nfe_info[$key]['url_danfe_etiqueta'] = str_replace('/danfe/', '/danfe/etiqueta/', $nfe['url_danfe']); 
   }
 }

 $data['nfe_info'] = $nfe_info;
 $data['url'] = $url;
}else{
 $data['nfe_info'] = array();
 $data['url'] = '';
}

